package WrapperClassesDemo;

public class CharacterClassExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
System.out.println(Character.toUpperCase('a'));
System.out.println(Character.isLetter('6'));

System.out.println(Character.isLetter('B'));
System.out.println(Character.isLowerCase('s'));
System.out.println(Character.isUpperCase('s'));

System.out.println(Character.isWhitespace('\n'));

	}

}
